from Longest_Palindrome import Solution


def test1():
    s = Solution()
    inputStr = 'abccccdd'
    ans = s.longestPalindrome(inputStr)
    assert ans == 7


def test2():
    s = Solution()
    inputStr = 'aaabbbccccddddd'
    ans = s.longestPalindrome(inputStr)
    assert ans == 13


def test3():
    s = Solution()
    inputStr = ''
    ans = s.longestPalindrome(inputStr)
    assert ans == 983
